import React, { Component } from 'react';
import ReactPaginate from 'react-paginate';
import _ from 'lodash'
import Loader from './Loader/Loader'
import DetailRowWiev from './DetailRowView/DetailRowWiev'
import Table from './Table/Table'
import ModeSelectorWiev from './ModelSelector/ModeSelector'
class App extends Component {
  state={
    isModeSeleced:false,
    isLoading:false,
    data:[],
    sort:'asc',
    sortField:'id',
    row:null
  }
  async fetchData(url){
    const response = await fetch(url)
    const data = await response.json();
    this.setState({
      data,
      isLoading:false,
      data: _.orderBy(data,this.state.sortField,this.state.sort)
    })
    console.log(this.state.data.length)
  }
  onSort = sortField =>{
    const clonedData = this.state.data.concat();
    const sort = this.state.sort === 'asc'?'desc':'asc'
    const data = _.orderBy(clonedData,sortField,sort)
    this.setState({data,sort,sortField})
  }
  modeSelectHandler = url =>{
    this.setState({
      isModeSeleced:true,
      isLoading:true
    })
    this.fetchData(url)
  }
  onRowSelect = row =>{
    this.setState({row})
  }
  PageChangeHandler = page =>{
    console.log(page)
  }
    render() {
    const pageSize= 50
    if(!this.state.isModeSeleced){
      return (
        <div className='container'>
          <ModeSelectorWiev onSelect={this.modeSelectHandler}/>
        </div>
      )
    }
    return (
      <div className="container">
        {this.state.isLoading
           ? <Loader/>
           : <Table
              data={this.state.data}
              onSort={this.onSort}
              sort={this.state.sort}
              sortField={this.state.sortField}
              onRowSelect={this.onRowSelect}
           />
        }
        {
          this.state.data.length > pageSize 
          ?   
          <ReactPaginate
          previousLabel={'<'}
          nextLabel={'>'}
          breakLabel={<a href="">...</a>}
          breakClassName={'break-me'}
          pageCount={20}
          marginPagesDisplayed={2}
          pageRangeDisplayed={10}
          onPageChange={this.handlePageClick}
          containerClassName={'pagination'}
          subContainerClassName={'pages pagination'}
         activeClassName={'active'}
      /> 
        :null
        }
        {
          this.state.row
           ? <DetailRowWiev person={this.state.row}/>
           : null
        }
      </div>
    );
  }
}

export default App;
